import streamlit as st
import time

featured_games = [
    {
        "title": "Snake",
        "image": "https://coopboardgames.com/wp-content/uploads/2025/10/Google-Snake-Game.jpeg",
    },
    {
        "title": "Tetris",
        "image": "data:image/jpeg;base64,",
    },
    {
        "title": "Dodge The Zombies",
        "image": "https://vampire.survivors.wiki/images/thumb/Mad_Forest_gameplay.jpg/640px-Mad_Forest_gameplay.jpg?7fe12",
    },

]

st.set_page_config(page_title="Featured Games Carousel", layout="wide")
st.markdown(
    """<style>
        .hero {
            position: relative;
            text-align: center;
            color: white;
        }
        .hero img {
            width: 100%;
            height: 80vh;
            object-fit: cover;
            filter: brightness(60%);
            border-radius: 10px;
        }
        .overlay {
            position: absolute;
            top: 50%;
            left: 50%;
            transform: translate(-50%, -50%);
        }
        .button {
            background: linear-gradient(90deg, #9333ea, #ec4899);
            color: white;
            border: none;
            padding: 15px 30px;
            font-size: 18px;
            border-radius: 30px;
            cursor: pointer;
        }
        .button:hover {
            opacity: 0.9;
        }
    </style>""",
    unsafe_allow_html=True,
)

placeholder = st.empty()

while True:
    for game in featured_games:
        with placeholder.container():
            st.markdown(f"""
            <div class='hero'>
                <img src='{game['image']}' alt='{game['title']}' />
                <div class='overlay'>
                    <h1 style='font-size: 60px; font-weight: bold; margin-bottom: 15px;'>Play {game['title']}</h1>
                    <p style='font-size: 20px; margin-bottom: 25px;'>Jump into action with our most popular featured games.</p>
                    <button class='button'>▶️ Play Now</button>
                </div>
            </div>
            """, unsafe_allow_html=True)
        time.sleep(4)